class Wallet:
    def __init__(self):
        self.balance = ,

    def add_cash(self, amount):
        if amount > 0:
            self.balance += amount
        else:
            print("Invalid amount. Amount must be greater than zero.")

    def spend_cash(self, amount):
        if amount <= self.balance and amount > 0:
            self.balance -= amount
        elif amount <= 0:
            print("Invalid amount. Amount must be greater than zero.")
        else:
            print("Insufficient balance.")

    def give_away(self, percentage):
        if percentage <= 100 and percentage > 0:
            amount_to_give = (self.balance / 100) * percentage
            self.balance -= amount_to_give
            return amount_to_give
        elif percentage <= 0:
            print("Invalid percentage. Percentage must be greater than zero.")
            return 0
        else:
            print("Invalid percentage. Percentage must not exceed 100.")
            return 0

